class StockTable extends React.Component {
  constructor(props) {
    super(props);
    this.prevColumns = [];

    this.state = {
      columns: ['date'],
      stock_name: ['BTC_USD'],
      data: {}
    }
  }

  getStockData() {
    const active_columns = this.props.active_columns;
    const stock_name = this.props.active_stock;
    fetch(`http://localhost:3001/get/${stock_name}/${active_columns}`)
    .then(response => {
      return response.json();
    })
    .then(fetched_data => {
      this.setState({
        data: fetched_data
      });
    })
  }

  render(){
   if (this.props.active_columns !== this.prevColumns || this.props.active_stock !== this.prevStock){
     this.getStockData();
     this.prevColumns = this.props.active_columns;
     this.prevStock = this.props.active_stock;
   }

    const isDataEmpty = Object.keys(this.state.data).length === 0;
    if (!isDataEmpty) { 
      // return <BootstrapTable keyField='name' data = {this.state.data}/>
      return <JSONTable data={this.state.data}/>; 
    }
    else{
      return <div> No hay datos disponibles </div>
    }
  }
}

class MultipleStockTable extends React.Component {
  constructor(props) {
    super(props);
    this.prevColumns = [];
    this.prevStockList = [];

    this.state = {
      columns: ['date'],
      data: {}
    }
  }

componentDidMount(){
  this.getStocksData();
}

componentDidUpdate(){
  if (this.props.active_columns !== this.prevColumns || this.props.stocks_list !== this.prevStockList){
    this.getStocksData();
    this.prevColumns = this.props.active_columns;
    this.prevStockList = this.props.stocks_list;
  }
}

 async getStocksData() {
    const active_columns = this.props.active_columns;
    const stocks_list = this.props.stocks_list;
    var stocksData = [];

  if (active_columns.length === 0) {
    for (let stock_name of stocks_list){
      var new_row = {};
      new_row['name'] = stock_name;

      stocksData.push(new_row)

      this.setState({data: stocksData});
    }
    return;
  }

    for await (let stock_name of stocks_list){
      fetch(`http://localhost:3001/get/${stock_name}/${active_columns}`)
      .then(response => {
        return response.json();
      })
      .then(fetched_data => {
        var new_row = {
          'name': fetched_data['stock_name']
        }
        Object.assign(new_row, fetched_data['data'][0]);

        stocksData.push(new_row)

        // if (this.props.active_columns.includes('rsi_3d')){
        //   this.sortByColumn(stocksData, 'rsi_3d')
        // }
        // else {
        //   this.sortByColumn(stocksData, 'name');
        // }

        this.setState({
          data: stocksData
        })
      })
    }

  }

  sortByColumn(data, column, desc=false){
    data.sort( (a,b) => {
      if (a[column] < b[column]) {return -1};
      if (a[column] > b[column]) {return 1};
      return 0;
    } );
    if (desc) data.reverse();
  }

  render(){
    let active_columns = ['name'].concat(this.props.active_columns);

    const columns = active_columns.map( (col) => {
      let columnData = {
        dataField: col,
        text: this.props.columnInfo[col]['text'],
        sort: true,
        type: this.props.columnInfo[col]['type']
      }
      if (this.props.columnManage && this.props.columnManage[col]){
        columnData['formatter'] = this.props.columnInfo[col]['formatter']
      }
      if (this.props.columnInfo[col]['sortFunc']){
        columnData['sortFunc'] = this.props.columnInfo[col]['sortFunc'];
      }
      if (this.props.columnInfo[col]['style']){
        columnData['style'] = this.props.columnInfo[col]['style'];
      }

      return(columnData);
    })


     const isDataEmpty = Object.keys(this.state.data).length === 0;
     if (!isDataEmpty) { 
       return (
         <BootstrapTable keyField='name' data={this.state.data} columns={columns}/>
       );
       return <JSONTable 
       data = {this.state.data}
       headers = {this.props.headers}
       columnManage = {this.props.columnManage}/>;
     }
     else{
       return <div>No hay datos disponibles</div>
     }
   }

}

